import styled from "styled-components";
import { useState } from "react";

const RollDice = ({setCurrentDice,currentDice}) => {
    
    const [diceImagePath, setDiceImagePath] = useState(`/images/dice/dice_1.png`);

    const roleDice = () => {
        const randomNumber = generateRandomNumber(1, 7);
        setCurrentDice((prev)=>randomNumber)
        setDiceImagePath(`/images/dice/dice_${randomNumber}.png`);;
    };

    const generateRandomNumber = (min, max) => {
        return Math.floor(Math.random() * (max - min) + min);
    };

    
    const altText = `dice image ${currentDice}`;

    return (
        <DiceContainer>
            <div className="dice" onClick={roleDice}>
                <img src={diceImagePath} alt={altText} />
                <p>Click on Dice to roll</p>
                
                
            </div>
        </DiceContainer>
    );
};

export default RollDice;

const DiceContainer = styled.div`
    display: flex;
    margin-top: 55px;
    flex-direction: column;
    align-items: center;

    img {
        width: 300px;
        height: 300px;
    }

    p {
        justify-content: center;
    }
`;
