import styled from "styled-components";
import { useState } from "react";
import dice1 from "../src/images/dice_1.png";
import dice2 from "../src/images/dice_2.png";
// Import other dice images as needed

const RollDice = ({ roleDice, currentDice }) => {
  // Assuming you want to dynamically change the dice image based on currentDice

  // Generate a map of dice images for easy access
  const diceImages = {
    1: dice1,
    2: dice2,
    3: dice3,
    4: dice4,
    5: dice5,
    6: dice6,

    // Add other dice images as needed
  };

  return (
    <DiceContainer>
      <div className="dice" onClick={roleDice}>
        <img src={diceImages[currentDice]} alt={`dice ${currentDice}`} />
      </div>
      <p>Click on Dice to roll</p>
    </DiceContainer>
  );
};

export default RollDice;

const DiceContainer = styled.div`
  display: flex;
  margin-top: 55px;
  flex-direction: column;
  align-items: center;

  img {
    width: 300px;
    height: 300px;
  }

  p {
    justify-content: center;
  }
`;
